function capFirst(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
}

let businessName;

const getBusinessName = () => {
    let businessName1 = business1[(Math.floor(Math.random() * business1.length))];
    
    let businessName2 = business2[(Math.floor(Math.random() * business2.length))];
    
    businessName = capFirst(businessName1) + " " + capFirst(businessName2);
    
    return "You've changed the Business Name!"
}


const getBusiness = () => {
    businessField = field[(Math.floor(Math.random() * field.length))];

    businessRetail = retail[(Math.floor(Math.random() * retail.length))];

    businessSize = size[(Math.floor(Math.random() * size.length))];

    businessAspiration = aspiration[(Math.floor(Math.random() * aspiration.length))];

    return "You've pivoted your business!"
}

const getClient = () => {
    first = firstNames[(Math.floor(Math.random() * firstNames.length))];

    last = lastNames[(Math.floor(Math.random() * lastNames.length))];
    
    clientName = `${first} ${last}`;
    
    clientRole = role[(Math.floor(Math.random() * role.length))];

    clientCity = city[(Math.floor(Math.random() * city.length))];

    clientMainStyle = mainStyle[(Math.floor(Math.random() * mainStyle.length))];

    clientSecondaryStyle = secondaryStyle[(Math.floor(Math.random() * secondaryStyle.length))];

    clientHobby = hobby[(Math.floor(Math.random() * hobby.length))];

    clientFavColor = favColor[(Math.floor(Math.random() * favColor.length))];

    clientNumber = (Math.floor(Math.random() * 100));

    return "That's a new person right there!"
}

const getDesign = () => {
    designColorRule = colorRule[(Math.floor(Math.random() * colorRule.length))];

    designLetters = letters[(Math.floor(Math.random() * letters.length))];
    
    designFontStyle = fontStyle[(Math.floor(Math.random() * fontStyle.length))];
    
    designMain = (Math.floor((Math.random() * 3) + 1));
    
    designSecondary = (Math.floor((Math.random() * 6) + 1));
    
    designTrend = trend[(Math.floor(Math.random() * trend.length))];
    
    return "Hope these are more to your liking!"
}

const getFlavor = () => {
    flavorHi = hi[(Math.floor(Math.random() * hi.length))];

    flavorReferral = referral[(Math.floor(Math.random() * referral.length))];
    
    flavorGoodbye = goodbye[(Math.floor(Math.random() * goodbye.length))];
    
    return "Was that really necessary?"
}

const getDeliverables = () => {
    deliverableUI = UI[(Math.floor(Math.random() * UI.length))];

    deliverableMotion = motion[(Math.floor(Math.random() * motion.length))];

    return "Get that behance shining!"
}

